<style lang="scss" scoped>
.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  width: 100%;

  @media (max-width: 500px) {
    position: fixed;
    background-color: #fff;
  }

  &.active {
    transform: translateX(0);

    .overlay {
      display: block;
    }
  }
}

button {
  display: none;
  border: 0;
  background: transparent;

  @media (max-width: 500px) {
    display: block;
  }

  svg {
    width: 20px;
    stroke: #3b847f;
  }
}

.overlay {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.548);
  display: none;
}

.link-container {
  display: flex;

  @media (max-width: 500px) {
    flex-direction: column;
    position: fixed;
    left: 0;
    top: 80px;
    bottom: 0;
    width: 250px;
    background-color: #fff;
    transform: translateX(-100%);
    transition: transform 0.4s ease-in-out;
    padding-top: 20px;
    padding-left: 20px;
  }
